import React from "react";
import { TextInput, View } from "react-native";
import { styled } from "nativewind";

const StyledTextInput = styled(TextInput);
const StyledView = styled(View);

function TextInputComponent({ onChangeText, value, placeholder, className, state }) {
  // State for the input text
  return (
    <StyledView className={"w-12"}>
      <StyledTextInput
        className={
          "text-xl text-teal-100 my-4 text-center border-solid border-2 border-teal-400 rounded-lg"
        }
        onChangeText={onChangeText}
        value={value}
        placeholder={placeholder}sta
      />
    </StyledView>
  );
}

export default TextInputComponent;
